import * as S from './styled';

import axios from "axios";
import { useEffect, useState } from "react";
import AdList from '../../component/AdList/AdList';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

interface memolist {
    id:Number
    value:string,
}
const Main = () => {

    const [memo, setMemo] = useState<memolist[]>([{id:0, value:''}])
    const [selectedDate, setselectedDateDate] = useState<string>();

    console.log(selectedDate)
    // console.log(date)

    const changeDate = (date:any) => {
        var year = date.getFullYear();
        var month = ('0' + (date.getMonth() + 1)).slice(-2);
        var day = ('0' + date.getDate()).slice(-2);
        var yyyyMMdd = year + month  + day;
        setselectedDateDate(yyyyMMdd)
    }

    // const yyyyMMdd = new Intl.DateTimeFormat('kr').format(new Date());

    useEffect(()=> {
        axios
        .get(`http://34.22.82.239:8080/getUserList?date=${selectedDate}`)
        // .get(`http://34.22.82.239:8080/getUserList?data=${yyyyMMdd}`)
        .then((res) => {
          console.log(res.data.users)

        })
        .catch((error) => {
          console.log(error);
        });
      },[])

      const memoChange = (e:any) => {
        // setMemo(...memo,id:id+1,value: e.target.value)
        console.log(memo)
      }
        const handleLogin = (e:any) => {
            setMemo(e.target.value)      
        }

        console.log(memo)
      const enterKeyPress = (e:any) => {
        if (e.key === "Enter") {
          handleLogin(e);
        }

    }

    // const xhr = new XMLHttpRequest();
    // xhr.open("GET", `http://34.22.82.239:8080/getUserList?date=20230425`);
    // xhr.onload = function() {
    //   if (xhr.status === 200) {
    //     console.log(xhr.responseText);
    //   }
    // };
    // xhr.send();


    return(
        <S.Main>
            <S.advertisementMemo>
                <S.advertisement>
                    <div>광고</div>
                        <AdList/>
                </S.advertisement>
                <S.Memo>
                    <div>메모란</div>
                    <div>
                        {/* <div>{memo.value}</div> */}
                        <input
                        placeholder='메모 입력란'
                        onChange={memoChange}
                        onKeyPress={enterKeyPress}
                        ></input>
                        <button onClick={() => handleLogin}>작성</button>
                    </div>
                </S.Memo>
            </S.advertisementMemo>
            <S.customer>
            <div>손님 4명</div>
            <DatePicker
                dateFormat="yyyyMMdd" // 날짜 형식 설정
                className="input-datepicker" // 클래스 명 지정 css주기 위해
                // minDate={new Date()} // 선택할 수 있는 최소 날짜값 지정
                closeOnScroll={true} // 스크롤을 움직였을 때 자동으로 닫히도록 설정 기본값 false
                // placeholderText="공동구매 시작일 선택" // placeholder
                // selected={startDate} // value  // 날짜를 선택하였을 때 실행될 함수
                onChange={(date: Date) => changeDate(date)}
              />
            </S.customer>
            <S.Search>
                <div>손님 검색 필터</div>
            </S.Search>

        </S.Main>
    )
}

export default Main;
